import { Product, Cart, Order, DiscountCode, AdminStats } from '../types';

class InMemoryDatabase {
    private products: Product[] = [
        {
            id: '1',
            name: 'Wireless Headphones',
            price: 99.99,
            description: 'High-quality wireless headphones with noise cancellation',
            image: 'data:image/jpeg;base64,',
            stock: 50
        },
        {
            id: '2',
            name: 'Smartphone',
            price: 699.99,
            description: 'Latest smartphone with advanced features',
            image: 'data:image/jpeg;base64,',
            stock: 30
        },
        {
            id: '3',
            name: 'Laptop',
            price: 1299.99,
            description: 'Powerful laptop for work and gaming',
            image: 'data:image/jpeg;base64,',
            stock: 20
        },
        {
            id: '4',
            name: 'Smart Watch',
            price: 199.99,
            description: 'Feature-rich smartwatch with health monitoring',
            image: 'data:image/jpeg;base64,',
            stock: 40
        }
    ];

    private carts: Map<string, Cart> = new Map();
    private orders: Order[] = [];
    private discountCodes: DiscountCode[] = [];
    private orderCounter: number = 0;

    // Product methods
    getProducts(): Product[] {
        console.log('ðŸ“¦ Returning products:', this.products.length);
        console.log('ðŸ“¦ Product details:', this.products.map(p => ({ id: p.id, name: p.name, price: p.price })));
        return this.products;
    }


    getProduct(id: string): Product | undefined {
        return this.products.find(p => p.id === id);
    }

    // Cart methods
    getCart(userId: string): Cart {
        if (!this.carts.has(userId)) {
            this.carts.set(userId, {
                id: userId,
                userId,
                items: [],
                total: 0,
                discountAmount: 0
            });
        }
        return this.carts.get(userId)!;
    }

    updateCart(userId: string, cart: Cart): void {
        this.carts.set(userId, cart);
    }

    // Order methods
    createOrder(order: Order): void {
        this.orders.push(order);
        this.orderCounter++;
    }

    getOrders(): Order[] {
        return this.orders;
    }

    // Discount code methods
    addDiscountCode(code: DiscountCode): void {
        this.discountCodes.push(code);
    }

    getDiscountCodes(): DiscountCode[] {
        return this.discountCodes;
    }

    updateDiscountCode(code: string, updates: Partial<DiscountCode>): void {
        const discountCode = this.discountCodes.find(dc => dc.code === code);
        if (discountCode) {
            Object.assign(discountCode, updates);
        }
    }

    // Admin stats
    getAdminStats(): AdminStats {
        const totalItemsPurchased = this.orders.reduce((sum, order) =>
            sum + order.items.reduce((itemSum, item) => itemSum + item.quantity, 0), 0
        );

        const totalPurchaseAmount = this.orders.reduce((sum, order) => sum + order.total, 0);
        const totalDiscountAmount = this.orders.reduce((sum, order) => sum + order.discountAmount, 0);

        return {
            totalItemsPurchased,
            totalPurchaseAmount,
            discountCodes: this.discountCodes,
            totalDiscountAmount
        };
    }

    getOrderCount(): number {
        return this.orderCounter;
    }

    // Add this method to clear cart
    clearCart(userId: string): void {
        this.carts.set(userId, {
            id: userId,
            userId,
            items: [],
            total: 0,
            discountAmount: 0
        });
    }

    private userOrderCounts: Map<string, number> = new Map();

    getUserOrderCount(userId: string): number {
        return this.userOrderCounts.get(userId) || 0;
    }

    incrementUserOrderCount(userId: string): void {
        const currentCount = this.getUserOrderCount(userId);
        this.userOrderCounts.set(userId, currentCount + 1);
    }
}

export const database = new InMemoryDatabase();